import React, { useState } from "react";
import "./LogIn.css";
import "../home/Home.css";
import translate from "../../i18n/messages/translate";
import { useSelector, useDispatch } from "react-redux";
import { increment, decrement, logIn } from "../../actions/index";
import { FormControl, FormGroup } from "react-bootstrap";

function LogIn() {
  const dispatch = useDispatch();
  const loggedIn = useSelector((state) => state.isLoggedIn);
  const [email, setEmail] = useState("");
  const [pwd, setPwd] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
  };
  return (
    <div className="home-container">
      {/*  <p>{translate("logintext")}</p>
      <button onClick={() => dispatch(logIn())}>Log in</button>
      {loggedIn ? (
        <h1 style={{ color: "green" }}>You are logged in</h1>
      ) : (
        <h1 style={{ color: "red" }}>You are not logged in yet!</h1>
      )} */}

      <div className="login-wrapper">
        <h1>Welcome to LoMLM!</h1>
        <form onSubmit={handleSubmit}>
          <FormGroup controlId="email">
            {/* <label htmlFor="login-label">username:</label>
            <br />
            <input className="form-input" type="email" name="email" /> */}
            <FormControl
              autoFocus
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </FormGroup>
          <br />
          <FormGroup controlId="password">
            <label htmlFor="login-label">password:</label>
            <br />
            <input className="form-input" type="password" name="password" />
          </FormGroup>
          <br />
          <button
            className="submit"
            type="submit"
            onClick={() => dispatch(logIn())}
          >
            Log in
          </button>
        </form>
        <a className="registration-link" href="#">
          Sign up
        </a>
      </div>
    </div>
  );
}
export default LogIn;
